import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { FaSignOutAlt, FaClipboardList, FaCheckCircle, FaHourglassHalf } from "react-icons/fa";

export default function AdminDashboard() {
  const [complaints, setComplaints] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("isAdmin");
    navigate("/admin-login");
  };

  const total = complaints.length;
  const pending = complaints.filter(c => c.status === "Pending").length;
  const resolved = complaints.filter(c => c.status === "Resolved").length;

  useEffect(() => {
    if (localStorage.getItem("isAdmin") !== "true") {
      alert("Only admins can access the dashboard.");
      navigate("/admin-login");
      return;
    }

    const fetchComplaints = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/admin/complaints", {
          withCredentials: true,
        });
        if (res.data.success) {
          setComplaints(res.data.complaints);
        } else {
          setError(res.data.message);
        }
      } catch (err) {
        setError("Failed to fetch complaints");
      }
    };
    fetchComplaints();
  }, []);

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-blue-900 text-white p-6">
        <h2 className="text-2xl font-bold mb-8">Admin Panel</h2>
        <ul className="space-y-6 text-lg">
          <li className="flex items-center gap-2 hover:text-yellow-300 cursor-pointer">
            <FaClipboardList /> Complaints
          </li>
          <li
            onClick={handleLogout}
            className="flex items-center gap-2 hover:text-red-400 cursor-pointer mt-10"
          >
            <FaSignOutAlt /> Logout
          </li>
        </ul>
      </div>

      {/* Main */}
      <div className="flex-1 p-8 overflow-auto">
        <h1 className="text-3xl font-bold mb-6 text-blue-900">📋 Admin Dashboard</h1>

        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-white shadow p-6 rounded-lg text-center">
            <FaClipboardList className="text-blue-600 text-3xl mx-auto mb-2" />
            <h2 className="text-xl font-bold">Total Complaints</h2>
            <p className="text-2xl text-gray-700 mt-1">{total}</p>
          </div>
          <div className="bg-white shadow p-6 rounded-lg text-center">
            <FaHourglassHalf className="text-yellow-600 text-3xl mx-auto mb-2" />
            <h2 className="text-xl font-bold">Pending</h2>
            <p className="text-2xl text-gray-700 mt-1">{pending}</p>
          </div>
          <div className="bg-white shadow p-6 rounded-lg text-center">
            <FaCheckCircle className="text-green-600 text-3xl mx-auto mb-2" />
            <h2 className="text-xl font-bold">Resolved</h2>
            <p className="text-2xl text-gray-700 mt-1">{resolved}</p>
          </div>
        </div>

        {/* Table */}
        {error && <p className="text-red-600 mb-4">{error}</p>}
        <div className="overflow-x-auto rounded shadow bg-white">
          <table className="min-w-full text-left border border-gray-300">
            <thead className="bg-blue-100 text-gray-700">
              <tr>
                <th className="py-3 px-4 border">ID</th>
                <th className="py-3 px-4 border">Name</th>
                <th className="py-3 px-4 border">Location</th>
                <th className="py-3 px-4 border">Department</th>
                <th className="py-3 px-4 border">Status</th>
                <th className="py-3 px-4 border">Token</th>
                <th className="py-3 px-4 border">Date</th>
              </tr>
            </thead>
            <tbody>
              {complaints.map((c) => (
                <tr key={c.id} className="hover:bg-gray-50 text-sm">
                  <td className="py-2 px-4 border">{c.id}</td>
                  <td className="py-2 px-4 border">{c.name}</td>
                  <td className="py-2 px-4 border">{c.location}</td>
                  <td className="py-2 px-4 border">{c.department}</td>
                  <td className="py-2 px-4 border">{c.status}</td>
                  <td className="py-2 px-4 border text-xs">{c.token}</td>
                  <td className="py-2 px-4 border text-xs">
                    {new Date(c.timestamp).toLocaleString()}
                  </td>
                </tr>
              ))}
              {complaints.length === 0 && !error && (
                <tr>
                  <td colSpan="7" className="text-center py-4 text-gray-500">
                    No complaints found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
