import React, { useState } from "react";
import axios from "axios";
import { FaMicrophone } from "react-icons/fa";

const VoiceComplaint = () => {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [complaint, setComplaint] = useState(null);

  const appendLog = (text) => {
    setLogs((prevLogs) => [...prevLogs, text]);
  };

  const startVoiceComplaint = async () => {
    try {
      setLogs([]);
      setComplaint(null);
      setLoading(true);
      appendLog("üéôÔ∏è Voice complaint process started...");

      const response = await axios.get("http://localhost:5000/api/voice-complaint");
      
      if (response.data.status === "success") {
        appendLog("‚úÖ Complaint captured successfully!");
        setComplaint(response.data.data);
        appendLog(JSON.stringify(response.data.data, null, 2));
      } else {
        appendLog("‚ùå Error: " + response.data.message);
      }

    } catch (error) {
      appendLog("‚ùå Error occurred: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 px-4 py-8">
      <div className="max-w-3xl mx-auto bg-white p-6 rounded-2xl shadow-lg">
        <h2 className="text-3xl font-bold mb-4 text-center text-indigo-700">üé§ Voice Complaint</h2>
        <p className="mb-4 text-gray-600 text-center">
          Press the button below and start speaking your complaint in Hindi. Our system will capture, understand, and submit it automatically.
        </p>

        <div className="flex justify-center mb-6">
          <button
            onClick={startVoiceComplaint}
            disabled={loading}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-6 py-3 rounded-full flex items-center gap-2 disabled:opacity-60"
          >
            <FaMicrophone className="text-lg" />
            {loading ? "Listening..." : "Start Voice Complaint"}
          </button>
        </div>

        <div className="bg-black text-green-400 p-4 rounded-lg h-64 overflow-y-scroll text-sm font-mono shadow-inner">
          {logs.length === 0 ? (
            <p className="text-gray-400">Logs will appear here...</p>
          ) : (
            logs.map((log, index) => <p key={index}>{log}</p>)
          )}
        </div>

        {complaint && (
          <div className="mt-6 bg-green-100 text-green-800 p-4 rounded-lg shadow">
            ‚úÖ Complaint submitted and forwarded to Admin Dashboard & Telegram!
          </div>
        )}
      </div>
    </div>
  );
};

export default VoiceComplaint;
