import React, { useState } from "react";
import axios from "axios";

export default function TrackStatus() {
  const [complaintId, setComplaintId] = useState("");
  const [status, setStatus] = useState(null);
  const [error, setError] = useState(null);

  const handleTrack = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/complaints/${complaintId}`);
      setStatus(res.data.status);
      setError(null);
    } catch (err) {
      setStatus(null);
      setError("Complaint not found or server error");
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold text-gray-800 mb-4">Track Complaint Status</h2>
      <input
        type="text"
        placeholder="Enter Complaint ID"
        value={complaintId}
        onChange={(e) => setComplaintId(e.target.value)}
        className="w-full border px-3 py-2 rounded mb-4"
      />
      <button
        onClick={handleTrack}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Check Status
      </button>
      {status && (
        <p className="mt-4 text-green-700 font-semibold">Status: {status}</p>
      )}
      {error && (
        <p className="mt-4 text-red-600">{error}</p>
      )}
    </div>
  );
}
