// Home.jsx
import React from "react";
import { useNavigate } from "react-router-dom";

export default function Home() {
  const navigate = useNavigate();

  return (
    <div className="flex flex-col items-center justify-center min-h-[80vh] bg-gradient-to-br from-gray-100 to-white px-6">
      <h1 className="text-4xl font-bold text-gray-800 mb-6">
        üõ†Ô∏è JantaVoice ‚Äì Voice-Powered Grievance Platform
      </h1>
      <p className="text-gray-600 text-lg text-center max-w-xl mb-10">
        Register complaints with ease ‚Äî using voice or text. Seamless experience for both citizens and administrators.
      </p>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 w-full max-w-xl">
        {/* Citizen */}
        <div
          onClick={() => navigate("/complaint")}
          className="cursor-pointer bg-white rounded-2xl shadow-md hover:shadow-lg transition-all duration-300 border-t-4 border-blue-500 p-6 text-center"
        >
          <h2 className="text-xl font-semibold text-gray-800 mb-2">üé§ Citizen Portal</h2>
          <p className="text-gray-600 text-sm">Lodge a complaint via text or voice in Hindi or Hinglish</p>
        </div>

        {/* Admin */}
        <div
          onClick={() => navigate("/admin")}
          className="cursor-pointer bg-white rounded-2xl shadow-md hover:shadow-lg transition-all duration-300 border-t-4 border-green-500 p-6 text-center"
        >
          <h2 className="text-xl font-semibold text-gray-800 mb-2">üõ°Ô∏è Admin Panel</h2>
          <p className="text-gray-600 text-sm">Manage complaints, track statuses and view analytics</p>
        </div>
      </div>
    </div>
  );
}
