import React, { useState } from "react";
import axios from "axios";

export default function VoiceComplaint() {
  const [voiceText, setVoiceText] = useState("");
  const [complaintId, setComplaintId] = useState(null);
  const [submitted, setSubmitted] = useState(false);

  const handleVoiceChange = (e) => {
    setVoiceText(e.target.value);
  };

  const handleSubmit = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/voice-complaint", {
        voiceInput: voiceText
      });
      setComplaintId(res.data.complaintId);
      setSubmitted(true);
    } catch (err) {
      console.error("Voice Complaint Failed", err.message);
      alert("Voice complaint failed. Try again.");
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-semibold mb-4">Voice Complaint (Text Simulated)</h2>
      <textarea
        onChange={handleVoiceChange}
        placeholder="Simulate your spoken complaint here..."
        className="w-full border px-3 py-2 rounded"
        rows="5"
      />
      <button
        onClick={handleSubmit}
        className="mt-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
      >
        Submit
      </button>

      {submitted && (
        <div className="mt-6 text-green-700 font-semibold">
          Complaint Submitted!<br />
          Complaint ID: <span className="font-bold">{complaintId}</span>
        </div>
      )}
    </div>
  );
}
