import React, { useEffect, useState } from "react";
import axios from "axios";

export default function AdminDashboard() {
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(true); // State to manage loading
  const [error, setError] = useState(null); // State to manage error

  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/complaints");
        setComplaints(res.data);
      } catch (err) {
        console.error("Error loading complaints", err.message);
        setError("Failed to load complaints. Please try again later.");
      } finally {
        setLoading(false); // Set loading to false after fetching
      }
    };

    fetchComplaints();
  }, []);

  if (loading) return <div>Loading complaints...</div>; // Loading state
  if (error) return <div>{error}</div>; // Error state

  return (
    <div className="max-w-5xl mx-auto mt-10 p-6 bg-white shadow rounded">
      <h2 className="text-2xl font-semibold mb-4">Admin Dashboard</h2>
      {complaints.length === 0 ? (
        <p>No complaints yet.</p>
      ) : (
        <table className="w-full text-left border">
          <thead className="bg-gray-100">
            <tr>
              <th className="p-2">ID</th>
              <th className="p-2">Name</th>
              <th className="p-2">Complaint</th>
              <th className="p-2">Location</th>
              <th className="p-2">Urgency</th>
            </tr>
          </thead>
          <tbody>
            {complaints.map((c, i) => (
              <tr key={i} className="border-t">
                <td className="p-2">{c._id}</td>
                <td className="p-2">{c.name}</td>
                <td className="p-2">{c.complaint}</td>
                <td className="p-2">{c.location}</td>
                <td className="p-2">{c.urgency}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
