import React from "react";
import { useNavigate } from "react-router-dom";

export default function AdminDashboard() {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("isAdmin"); // ✅ removes admin session
    navigate("/admin-login"); // 🔁 back to login screen
  };

  return (
    <div className="flex">
      {/* Sidebar */}
      <div className="w-64 bg-gray-800 text-white min-h-screen p-6">
        <h2 className="text-2xl font-bold mb-6">Admin Panel</h2>
        <ul className="space-y-4">
          <li className="hover:text-blue-300 cursor-pointer">📋 View Complaints</li>
          <li className="hover:text-blue-300 cursor-pointer">📊 Analytics</li>
          <li
            onClick={handleLogout}
            className="hover:text-red-400 cursor-pointer font-semibold"
          >
            🚪 Logout
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-10">
        <h1 className="text-3xl font-bold mb-4">Welcome, Admin 👋</h1>
        <p className="text-gray-600">You can manage complaints and view analytics from here.</p>
      </div>
    </div>
  );
}
