import React, { useState } from "react";
import axios from "axios";

export default function ComplaintForm() {
  const [name, setName] = useState("");
  const [location, setLocation] = useState("");
  const [complaint, setComplaint] = useState("");
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/complaints", {
        name,
        location,
        complaint,
      });
      setResponse("✅ Complaint submitted successfully!");
      setName("");
      setLocation("");
      setComplaint("");
    } catch (error) {
      setResponse("❌ Failed to submit complaint.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold text-gray-800 mb-4">Register Complaint</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="w-full border rounded px-3 py-2"
        />
        <input
          type="text"
          placeholder="Your Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="w-full border rounded px-3 py-2"
        />
        <textarea
          placeholder="Describe your complaint"
          value={complaint}
          onChange={(e) => setComplaint(e.target.value)}
          rows="4"
          className="w-full border rounded px-3 py-2"
        ></textarea>
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          disabled={loading}
        >
          {loading ? "Submitting..." : "Submit"}
        </button>
      </form>
      {response && <p className="mt-4 text-sm text-gray-700">{response}</p>}
    </div>
  );
}
