import React, { useEffect, useState } from "react";
import axios from "axios";

function AdminDashboard() {
  const [complaints, setComplaints] = useState([]);

  const fetchComplaints = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/complaints");
      setComplaints(res.data.complaints);
    } catch (error) {
      console.error("Error fetching complaints", error);
    }
  };

  const updateStatus = async (id, newStatus) => {
    try {
      const res = await axios.post(`http://localhost:5000/api/admin/update-status/${id}`, {
        status: newStatus,
      });
      if (res.data.success) {
        fetchComplaints();
      }
    } catch (error) {
      console.error("Error updating status", error);
    }
  };

  useEffect(() => {
    fetchComplaints();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Admin Complaint Dashboard</h1>
      {complaints.map((complaint) => (
        <div key={complaint.id} className="bg-white p-4 rounded shadow mb-4">
          <p><strong>Name:</strong> {complaint.name}</p>
          <p><strong>Location:</strong> {complaint.location}</p>
          <p><strong>Department:</strong> {complaint.department}</p>
          <p><strong>Status:</strong> {complaint.status}</p>
          <p><strong>Token:</strong> {complaint.token}</p>
          <p><strong>Timestamp:</strong> {complaint.timestamp}</p>
          <div className="mt-2">
            <button
              onClick={() => updateStatus(complaint.id, "Resolved")}
              className="bg-green-500 text-white px-3 py-1 rounded mr-2"
            >
              Mark Resolved
            </button>
            <button
              onClick={() => updateStatus(complaint.id, "Pending")}
              className="bg-yellow-500 text-white px-3 py-1 rounded"
            >
              Mark Pending
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default AdminDashboard;
