import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function AdminDashboard() {
  const [complaints, setComplaints] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [selectedDept, setSelectedDept] = useState("All");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const isAdmin = localStorage.getItem("isAdmin");
    if (isAdmin !== "true") {
      alert("Only admins can access this page.");
      navigate("/admin-login");
      return;
    }

    const fetchComplaints = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/admin/complaints");
        if (res.data.success) {
          setComplaints(res.data.complaints);
          const uniqueDepts = [...new Set(res.data.complaints.map(c => c.department))];
          setDepartments(uniqueDepts);
        } else {
          setError(res.data.message || "Unknown error from server.");
        }
      } catch (err) {
        console.error("Fetch error:", err);
        setError("Failed to fetch complaints.");
      }
    };

    fetchComplaints();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("isAdmin");
    navigate("/admin-login");
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      const res = await axios.put(`http://localhost:5000/api/admin/update-status/${id}`, {
        status: newStatus,
      });

      if (res.data.success) {
        setComplaints(prev =>
          prev.map(c => (c.id === id ? { ...c, status: newStatus } : c))
        );
      } else {
        alert("Failed to update status: " + res.data.message);
      }
    } catch (err) {
      console.error("Status update failed:", err);
      alert("Error updating complaint status.");
    }
  };

  const filteredComplaints =
    selectedDept === "All"
      ? complaints
      : complaints.filter(c => c.department === selectedDept);

  const resolvedCount = complaints.filter(c => c.status === "Resolved").length;
  const pendingCount = complaints.filter(c => c.status === "Pending").length;

  return (
    <div className="bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="bg-blue-700 text-white flex justify-between items-center px-6 py-4 shadow">
        <h1 className="text-2xl font-bold">üõ†Ô∏è Admin Dashboard</h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 hover:bg-red-600 px-4 py-1 rounded text-sm"
        >
          Logout
        </button>
      </div>

      {/* Stats */}
      <div className="grid sm:grid-cols-3 gap-6 p-6">
        <div className="bg-white shadow-lg rounded-xl p-6 text-center">
          <h2 className="text-lg font-semibold">Total Complaints</h2>
          <p className="text-3xl font-bold text-blue-600">{complaints.length}</p>
        </div>
        <div className="bg-white shadow-lg rounded-xl p-6 text-center">
          <h2 className="text-lg font-semibold">Resolved</h2>
          <p className="text-3xl font-bold text-green-500">{resolvedCount}</p>
        </div>
        <div className="bg-white shadow-lg rounded-xl p-6 text-center">
          <h2 className="text-lg font-semibold">Pending</h2>
          <p className="text-3xl font-bold text-red-500">{pendingCount}</p>
        </div>
      </div>

      {/* Filter */}
      <div className="flex justify-between items-center px-6 mb-4">
        <h2 className="text-xl font-bold">üìã Complaints List</h2>
        <div className="flex items-center">
          <label className="mr-2 font-medium">Department:</label>
          <select
            value={selectedDept}
            onChange={(e) => setSelectedDept(e.target.value)}
            className="px-3 py-1 border rounded-md shadow-sm"
          >
            <option value="All">All</option>
            {departments.map((dept, idx) => (
              <option key={idx} value={dept}>
                {dept}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Error */}
      {error && <p className="text-red-600 px-6 mb-2">{error}</p>}

      {/* Table */}
      <div className="p-6 overflow-x-auto">
        <table className="w-full bg-white shadow rounded-lg text-sm">
          <thead className="bg-gray-100 text-gray-800">
            <tr>
              <th className="px-4 py-2 border">ID</th>
              <th className="px-4 py-2 border">Name</th>
              <th className="px-4 py-2 border">Location</th>
              <th className="px-4 py-2 border">Department</th>
              <th className="px-4 py-2 border">Status</th>
              <th className="px-4 py-2 border">Update</th>
              <th className="px-4 py-2 border">Token</th>
              <th className="px-4 py-2 border">Date</th>
            </tr>
          </thead>
          <tbody>
            {filteredComplaints.length > 0 ? (
              filteredComplaints.map(c => (
                <tr key={c.id} className="text-center hover:bg-gray-50">
                  <td className="border px-3 py-2">{c.id}</td>
                  <td className="border px-3 py-2">{c.name}</td>
                  <td className="border px-3 py-2">{c.location}</td>
                  <td className="border px-3 py-2">{c.department}</td>
                  <td className="border px-3 py-2">
                    <span
                      className={`text-xs px-2 py-1 rounded-full font-medium ${
                        c.status === "Resolved"
                          ? "bg-green-500 text-white"
                          : "bg-yellow-500 text-white"
                      }`}
                    >
                      {c.status}
                    </span>
                  </td>
                  <td className="border px-3 py-2">
                    <select
                      value={c.status}
                      onChange={(e) => handleStatusChange(c.id, e.target.value)}
                      className="text-sm border px-2 py-1 rounded"
                    >
                      <option value="Pending">Pending</option>
                      <option value="Resolved">Resolved</option>
                    </select>
                  </td>
                  <td className="border px-3 py-2">{c.token}</td>
                  <td className="border px-3 py-2">
                    {new Date(c.timestamp).toLocaleString()}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="8" className="text-center py-4 text-gray-500">
                  No complaints to display.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
