import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [complaints, setComplaints] = useState([]);

  useEffect(() => {
    fetchComplaints();
  }, []);

  const fetchComplaints = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/complaints", {
        withCredentials: true,
      });
      if (res.data.success) {
        setComplaints(res.data.complaints);
      }
    } catch (error) {
      console.error("Error fetching complaints:", error);
    }
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      const res = await axios.post("http://localhost:5000/api/admin/update-status", {
        id,
        status: newStatus,
      });
      if (res.data.success) {
        const updated = complaints.map((c) =>
          c.id === id ? { ...c, status: newStatus } : c
        );
        setComplaints(updated);
      }
    } catch (error) {
      console.error("Error updating status:", error);
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-4xl font-bold mb-6 text-center">Admin Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {complaints.map((complaint, index) => (
          <div key={index} className="bg-white shadow-lg rounded-xl p-6 hover:shadow-xl transition duration-300">
            <h2 className="text-xl font-semibold mb-2">{complaint.name}</h2>
            <p><strong>Complaint ID:</strong> {complaint.id}</p>
            <p><strong>Email:</strong> {complaint.email}</p>
            <p><strong>Department:</strong> {complaint.department}</p>
            <p><strong>Description:</strong> {complaint.complaint}</p>
            
            <div className="mt-4">
              <label className="font-semibold">Status: </label>
              <select
                value={complaint.status || "Pending"}
                onChange={(e) => handleStatusChange(complaint.id, e.target.value)}
                className="ml-2 px-3 py-1 rounded border border-gray-300 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-400"
              >
                <option value="Pending">Pending</option>
                <option value="Processing">Processing</option>
                <option value="Resolved">Resolved</option>
              </select>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
