import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function AdminDashboard() {
  const [complaints, setComplaints] = useState([]);
  const [error, setError] = useState("");
  const [departments, setDepartments] = useState([]);
  const [selectedDept, setSelectedDept] = useState("All");
  const navigate = useNavigate();

  useEffect(() => {
    const isAdmin = localStorage.getItem("isAdmin");
    if (isAdmin !== "true") {
      alert("Only admins can access this page.");
      navigate("/admin-login");
      return;
    }

    const fetchComplaints = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/admin/complaints", {
          withCredentials: true,
        });
        if (res.data.success) {
          setComplaints(res.data.complaints);
          const uniqueDepartments = [...new Set(res.data.complaints.map(c => c.department))];
          setDepartments(uniqueDepartments);
        } else {
          setError(res.data.message);
        }
      } catch (err) {
        setError("Failed to fetch complaints");
      }
    };

    fetchComplaints();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("isAdmin");
    navigate("/admin-login");
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      const res = await axios.put(`http://localhost:5000/api/admin/update-status/${id}`, {
        status: newStatus,
      });
      if (res.data.success) {
        setComplaints(prev =>
          prev.map(c => (c.id === id ? { ...c, status: newStatus } : c))
        );
      } else {
        alert("Failed to update status.");
      }
    } catch (err) {
      alert("Error updating complaint status.");
    }
  };

  const filteredComplaints = selectedDept === "All"
    ? complaints
    : complaints.filter(c => c.department === selectedDept);

  const resolvedCount = complaints.filter(c => c.status === "Resolved").length;
  const pendingCount = complaints.filter(c => c.status === "Pending").length;

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <div className="bg-blue-700 text-white flex justify-between items-center px-6 py-4 shadow">
        <h1 className="text-2xl font-bold">üõ†Ô∏è Admin Dashboard</h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 px-4 py-1 rounded hover:bg-red-600 text-sm"
        >
          Logout
        </button>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-6 p-6">
        <div className="bg-white shadow-md rounded-xl p-6 text-center">
          <h2 className="text-xl font-semibold">Total Complaints</h2>
          <p className="text-3xl font-bold text-blue-700">{complaints.length}</p>
        </div>
        <div className="bg-white shadow-md rounded-xl p-6 text-center">
          <h2 className="text-xl font-semibold">Resolved</h2>
          <p className="text-3xl font-bold text-green-600">{resolvedCount}</p>
        </div>
        <div className="bg-white shadow-md rounded-xl p-6 text-center">
          <h2 className="text-xl font-semibold">Pending</h2>
          <p className="text-3xl font-bold text-red-600">{pendingCount}</p>
        </div>
      </div>

      {/* Filter */}
      <div className="px-6 mb-4 flex items-center justify-between">
        <h2 className="text-xl font-bold">üìã Complaints List</h2>
        <div>
          <label className="mr-2 font-medium">Filter by Department:</label>
          <select
            value={selectedDept}
            onChange={(e) => setSelectedDept(e.target.value)}
            className="px-3 py-1 border rounded shadow-sm"
          >
            <option value="All">All</option>
            {departments.map((dept, i) => (
              <option key={i} value={dept}>{dept}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Error */}
      {error && <p className="text-red-500 px-6">{error}</p>}

      {/* Table */}
      <div className="p-6 overflow-x-auto">
        <table className="min-w-full bg-white border text-sm rounded-lg overflow-hidden">
          <thead className="bg-gray-200 text-gray-700">
            <tr>
              <th className="py-2 px-4 border">ID</th>
              <th className="py-2 px-4 border">Name</th>
              <th className="py-2 px-4 border">Location</th>
              <th className="py-2 px-4 border">Department</th>
              <th className="py-2 px-4 border">Status</th>
              <th className="py-2 px-4 border">Change Status</th>
              <th className="py-2 px-4 border">Token</th>
              <th className="py-2 px-4 border">Date</th>
            </tr>
          </thead>
          <tbody>
            {filteredComplaints.map(c => (
              <tr key={c.id} className="text-center hover:bg-gray-50">
                <td className="border py-2 px-3">{c.id}</td>
                <td className="border py-2 px-3">{c.name}</td>
                <td className="border py-2 px-3">{c.location}</td>
                <td className="border py-2 px-3">{c.department}</td>
                <td className="border py-2 px-3">
                  <span
                    className={`px-2 py-1 rounded-full text-white text-xs ${
                      c.status === "Resolved" ? "bg-green-600" : "bg-yellow-500"
                    }`}
                  >
                    {c.status}
                  </span>
                </td>
                <td className="border py-2 px-3">
                  <select
                    value={c.status}
                    onChange={(e) => handleStatusChange(c.id, e.target.value)}
                    className="border px-2 py-1 text-sm rounded"
                  >
                    <option value="Pending">Pending</option>
                    <option value="Resolved">Resolved</option>
                  </select>
                </td>
                <td className="border py-2 px-3 text-xs">{c.token}</td>
                <td className="border py-2 px-3">{new Date(c.timestamp).toLocaleString()}</td>
              </tr>
            ))}
            {filteredComplaints.length === 0 && (
              <tr>
                <td colSpan="8" className="text-center py-4 text-gray-500">
                  No complaints available for this department.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
