import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function AdminDashboard() {
  const [complaints, setComplaints] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const isAdmin = localStorage.getItem("isAdmin");
    if (isAdmin !== "true") {
      alert("Only admins can access this page.");
      navigate("/admin-login");
      return;
    }

    const fetchComplaints = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/admin/complaints", {
          withCredentials: true,
        });
        if (res.data.success) {
          setComplaints(res.data.complaints);
        } else {
          setError(res.data.message);
        }
      } catch (err) {
        setError("Failed to fetch complaints");
      }
    };

    fetchComplaints();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("isAdmin");
    navigate("/admin-login");
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      const res = await axios.put(
        `http://localhost:5000/api/admin/update-status/${id}`,
        { status: newStatus },
        { withCredentials: true }
      );

      if (res.data.success) {
        setComplaints(prev =>
          prev.map(c => (c.id === id ? { ...c, status: newStatus } : c))
        );
      }
    } catch (err) {
      alert("Failed to update status");
    }
  };

  const resolvedCount = complaints.filter(c => c.status === "Resolved").length;
  const pendingCount = complaints.filter(c => c.status === "Pending").length;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-white">
      {/* Header */}
      <div className="bg-blue-700 text-white flex justify-between items-center px-8 py-5 shadow-md">
        <h1 className="text-2xl font-bold tracking-wide">Admin Dashboard</h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 hover:bg-red-600 px-5 py-2 rounded-lg text-sm font-medium transition duration-300 shadow-md"
        >
          Logout
        </button>
      </div>

      {/* Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-6 p-8">
        <div className="bg-white shadow-lg rounded-2xl p-8 text-center border border-gray-100 hover:shadow-xl transition">
          <h2 className="text-lg font-semibold text-gray-700">Total Complaints</h2>
          <p className="text-4xl font-extrabold text-blue-700 mt-2">{complaints.length}</p>
        </div>
        <div className="bg-white shadow-lg rounded-2xl p-8 text-center border border-gray-100 hover:shadow-xl transition">
          <h2 className="text-lg font-semibold text-gray-700">Resolved</h2>
          <p className="text-4xl font-extrabold text-green-600 mt-2">{resolvedCount}</p>
        </div>
        <div className="bg-white shadow-lg rounded-2xl p-8 text-center border border-gray-100 hover:shadow-xl transition">
          <h2 className="text-lg font-semibold text-gray-700">Pending</h2>
          <p className="text-4xl font-extrabold text-red-600 mt-2">{pendingCount}</p>
        </div>
      </div>

      {/* Table */}
      <div className="px-8 pb-12">
        <h2 className="text-xl font-bold mb-4 text-gray-800">📋 Complaints List</h2>
        {error && <p className="text-red-500 mb-4">{error}</p>}
        <div className="overflow-x-auto rounded-xl border border-gray-200 shadow-sm bg-white">
          <table className="min-w-full text-sm text-gray-700">
            <thead className="bg-gray-100 text-gray-700 uppercase text-xs">
              <tr>
                <th className="py-3 px-5 border">ID</th>
                <th className="py-3 px-5 border">Name</th>
                <th className="py-3 px-5 border">Location</th>
                <th className="py-3 px-5 border">Department</th>
                <th className="py-3 px-5 border">Status</th>
                <th className="py-3 px-5 border">Token</th>
                <th className="py-3 px-5 border">Date</th>
              </tr>
            </thead>
            <tbody>
              {complaints.map(c => (
                <tr key={c.id} className="text-center hover:bg-gray-50 transition duration-150">
                  <td className="border py-2 px-4">{c.id}</td>
                  <td className="border py-2 px-4">{c.name}</td>
                  <td className="border py-2 px-4">{c.location}</td>
                  <td className="border py-2 px-4">{c.department}</td>
                  <td className="border py-2 px-4">
                    <select
                      value={c.status}
                      onChange={e => handleStatusChange(c.id, e.target.value)}
                      className={`border px-2 py-1 rounded-md text-sm ${
                        c.status === "Resolved"
                          ? "text-green-600"
                          : c.status === "Pending"
                          ? "text-yellow-600"
                          : "text-gray-600"
                      }`}
                    >
                      <option value="Pending">Pending</option>
                      <option value="Processing">Processing</option>
                      <option value="Resolved">Resolved</option>
                    </select>
                  </td>
                  <td className="border py-2 px-4 text-xs">{c.token}</td>
                  <td className="border py-2 px-4">{new Date(c.timestamp).toLocaleString()}</td>
                </tr>
              ))}
              {complaints.length === 0 && (
                <tr>
                  <td colSpan="7" className="text-center py-6 text-gray-400 font-medium">
                    No complaints available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
