import React, { useEffect, useState } from "react";
import axios from "axios";

export default function AdminDashboard() {
  const [complaints, setComplaints] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchComplaints();
  }, []);

  const fetchComplaints = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/complaints", {
        withCredentials: true,
      });
      if (res.data.success) {
        setComplaints(res.data.complaints);
      } else {
        setError("Unauthorized access. Please log in.");
      }
    } catch (err) {
      setError("Error fetching complaints.");
    }
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      const res = await axios.post("http://localhost:5000/api/admin/update-status", {
        id,
        status: newStatus,
      });
      if (res.data.success) {
        setComplaints((prev) =>
          prev.map((c) => (c.id === id ? { ...c, status: newStatus } : c))
        );
      } else {
        alert("Failed to update status");
      }
    } catch (err) {
      console.error(err);
      alert("Error updating status");
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Admin Dashboard</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border rounded shadow">
          <thead>
            <tr className="bg-gray-200 text-left">
              <th className="py-2 px-4">ID</th>
              <th className="py-2 px-4">Name</th>
              <th className="py-2 px-4">Department</th>
              <th className="py-2 px-4">Complaint</th>
              <th className="py-2 px-4">Status</th>
            </tr>
          </thead>
          <tbody>
            {complaints.map((complaint) => (
              <tr key={complaint.id} className="border-t">
                <td className="py-2 px-4">{complaint.id}</td>
                <td className="py-2 px-4">{complaint.name}</td>
                <td className="py-2 px-4">{complaint.department}</td>
                <td className="py-2 px-4">{complaint.complaint}</td>
                <td className="py-2 px-4">
                  <select
                    value={complaint.status}
                    onChange={(e) => handleStatusChange(complaint.id, e.target.value)}
                    className="border rounded px-2 py-1"
                  >
                    <option value="Pending">Pending</option>
                    <option value="Processing">Processing</option>
                    <option value="Resolved">Resolved</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
