import React, { useState } from "react";

export default function VoiceComplaint() {
  const [text, setText] = useState("");
  const [listening, setListening] = useState(false);

  const startListening = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) return alert("Your browser does not support speech recognition");
    const recognition = new SpeechRecognition();
    recognition.lang = "hi-IN";
    recognition.continuous = false;
    recognition.interimResults = false;

    recognition.onstart = () => setListening(true);
    recognition.onend = () => setListening(false);
    recognition.onresult = (e) => {
      const transcript = e.results[0][0].transcript;
      setText(transcript);
    };
    recognition.start();
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold text-gray-800 mb-4">Voice Complaint</h2>
      <button
        onClick={startListening}
        className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
      >
        {listening ? "Listening..." : "Start Speaking"}
      </button>
      <textarea
        className="w-full border mt-4 p-2 rounded"
        rows="4"
        value={text}
        readOnly
      ></textarea>
    </div>
  );
}
