import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [complaints, setComplaints] = useState([]);
  const [filteredComplaints, setFilteredComplaints] = useState([]);
  const [statusFilter, setStatusFilter] = useState("All");

  // Fetch complaints from backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/admin/complaints");
        setComplaints(res.data.complaints);
        setFilteredComplaints(res.data.complaints);
      } catch (err) {
        console.error("Error fetching complaints:", err);
        alert("Failed to fetch complaints. Please check backend.");
      }
    };
    fetchData();
  }, []);

  // Filter complaints by status
  const handleFilterChange = (e) => {
    const newStatus = e.target.value;
    setStatusFilter(newStatus);

    if (newStatus === "All") {
      setFilteredComplaints(complaints);
    } else {
      const filtered = complaints.filter((c) => c.status === newStatus);
      setFilteredComplaints(filtered);
    }
  };

  // Update complaint status
  const updateStatus = async (id, newStatus) => {
    try {
      const res = await axios.put(`http://localhost:5000/api/admin/update-status/${id}`, {
        status: newStatus,
      });

      if (res.data.success) {
        const updated = complaints.map((c) =>
          c.id === id ? { ...c, status: newStatus } : c
        );
        setComplaints(updated);
        if (statusFilter === "All") {
          setFilteredComplaints(updated);
        } else {
          setFilteredComplaints(updated.filter((c) => c.status === statusFilter));
        }
      } else {
        alert("Update failed: " + res.data.message);
      }
    } catch (err) {
      console.error("Status update failed:", err);
      alert("Error updating complaint status");
    }
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold text-center mb-6">Admin Complaint Dashboard</h1>

      <div className="mb-4 flex justify-center">
        <label className="mr-2 font-medium">Filter by Status:</label>
        <select
          value={statusFilter}
          onChange={handleFilterChange}
          className="p-2 border rounded"
        >
          <option value="All">All</option>
          <option value="Pending">Pending</option>
          <option value="In Progress">In Progress</option>
          <option value="Resolved">Resolved</option>
        </select>
      </div>

      <div className="grid gap-4">
        {filteredComplaints.map((complaint) => (
          <div
            key={complaint.id}
            className="bg-white shadow-md rounded-lg p-4 border-l-4"
            style={{
              borderColor:
                complaint.status === "Pending"
                  ? "#f97316"
                  : complaint.status === "In Progress"
                  ? "#3b82f6"
                  : "#22c55e",
            }}
          >
            <h2 className="text-xl font-semibold mb-1">{complaint.name}</h2>
            <p><strong>Department:</strong> {complaint.department}</p>
            <p><strong>Location:</strong> {complaint.location}</p>
            <p><strong>Priority:</strong> {complaint.priority}</p>
            <p><strong>Token:</strong> {complaint.token}</p>
            <p><strong>Description:</strong> {complaint.description}</p>
            <p><strong>Status:</strong> {complaint.status}</p>

            {complaint.status !== "Resolved" && (
              <div className="mt-3">
                {complaint.status !== "In Progress" && (
                  <button
                    className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-1 mr-2 rounded"
                    onClick={() => updateStatus(complaint.id, "In Progress")}
                  >
                    Mark In Progress
                  </button>
                )}
                <button
                  className="bg-green-600 hover:bg-green-700 text-white px-4 py-1 rounded"
                  onClick={() => updateStatus(complaint.id, "Resolved")}
                >
                  Mark Resolved
                </button>
              </div>
            )}
          </div>
        ))}

        {filteredComplaints.length === 0 && (
          <p className="text-center text-gray-500">No complaints found.</p>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
