// src/pages/UserOptions.jsx

import React from "react";
import { useNavigate } from "react-router-dom";

export default function UserOptions() {
  const navigate = useNavigate();

  const options = [
    {
      title: "Voice Complaint",
      description: "Speak and submit your complaint",
      icon: "🎙️",
      path: "/voice-complaint",
    },
    {
      title: "Written Complaint",
      description: "Fill out a complaint form",
      icon: "📝",
      path: "/complaint-form",
    },
    {
      title: "Track Your Complaint",
      description: "View your complaint status",
      icon: "🔍",
      path: "/trackstatus",
    },
  ];

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center px-4 py-10">
      <div className="bg-white rounded-2xl shadow-md w-full max-w-4xl p-10">
        <h2 className="text-3xl font-semibold text-center text-blue-700 mb-10">
          Select an Option
        </h2>

        <div className="grid grid-cols-1 sm:grid-cols-3 gap-8">
          {options.map((option) => (
            <div
              key={option.title}
              onClick={() => navigate(option.path)}
              className="cursor-pointer bg-gray-50 border hover:border-blue-500 transition rounded-xl shadow-sm hover:shadow-md p-6 flex flex-col items-center text-center"
            >
              <div className="text-4xl mb-4">{option.icon}</div>
              <h3 className="text-lg font-medium text-gray-800 mb-1">
                {option.title}
              </h3>
              <p className="text-sm text-gray-500">{option.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
